mapper.insert:

INSERT INTO GROUP_ACTIVITY_TB
(
	USER_NO,
	GROUP_NO
)
VALUES
(
	#{user_no},
	#{group_no}
)

mapper.update:

UPDATE ACTIVITY_TB SET
	TOTAL_ACT_DISTANCE = #{total_act_distance},
	TOTAL_ACT_TIME = #{total_act_time},
	SHARE_ST = #{share_st},
	ACT_ST = #{act_st}
WHERE ACT_NO = #{act_no};

mapper.select:

SELECT
	COUNT(*)
FROM USER_GROUP_TB
WHERE GROUP_NO = #{group_no}
AND USER_NO = #{user_no};

mapper.update:
UPDATE ACTIVITY_TB SET
   SHARE_ST = #{share_st}
WHERE ACT_NO = #{act_no}
